function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var whenneeded = g_form.getValue('u_when_needed');
	var today = new Date();
	var dd = today.getDate();
	var mm = today.getMonth()+1; //January is 0!
	var yyyy = today.getFullYear();

	  if(g_form.isNewRecord()){
        g_form.setValue('u_requested_for',g_user.userID);
		 // alert('This is a new record');
		  return;
    }

	if(dd<10) {
		dd = '0'+dd;
	} 

	if(mm<10) {
		mm = '0'+mm;
	} 

	today = yyyy + '-' + mm + '-' + dd;

	// 2018/04/18 Rod Fromm - This alert is displayed mltiple tie when form is loaded and doesn't clear When needed filed
	if (whenneeded < today) {
		alert('The When needed date can not be a past date');
		g_form.setValue('u_when_needed',"");
      return;
   }
alert('Return from ValidateDate function');

//Type appropriate comment here, and begin script below

}
